mapper.update:
update mem
set coin = #{coin}
where userid = #{userId}

mapper.select:
select dragonid 
from dragonlist
where eggid = #{productId}

mapper.update:
update dragoninfo
set equip = #{equip}
where userid = #{userId}
and dragonid = #{dragonId}

mapper.select:
select proimg as level0, level1, level2, level3
from dragonlist, product where dragonlist.eggid = product.proid 
and dragonid = #{dragonId}

select backgroundid 
from dragoninfo
where userid = #{userId}

mapper.update:
update dragoninfo
set backgroundid = #{backgroundId}
where userid = #{userId}
and dragonid = #{dragonId}

update dragoninfo
set foodvalue = #{foodValue}
where userid = #{userId}
and dragonid = #{dragonId}

mapper.delete:
delete from dragoninfo
where userid = #{userId}
and dragonid = #{dragonId}

mapper.select:
select eggid 
from dragonlist
where dragonid = #{dragonId}
